function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector("body"),c=document.querySelector(".socials"),o=document.querySelector(".hero");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"),c.classList.remove("active"),o.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"),setTimeout((()=>{c.classList.add("active")}),300),o.classList.add("locked"))}))}function toggleBtnClass(){const e=document.querySelector(".language-btn");e.addEventListener("click",(()=>{e.parentElement.classList.toggle("open")}))}burgerMenu(),toggleBtnClass();